import { getArtistAlbum, getArtistMv, getArtistDesc, getSimiArtist } from "../../plugins/artist.js";
import { getbr } from "../../general/regular.js";
import singerinfo from "../../components/singer/singerInfo.vue";
import navigation from "../../components/basics/navigation.vue";
import album from "./album.vue";
import MV from "./MV";
import singerDetails from "./singerDetails.vue";
import similaritySinger from "./similaritySinger.vue";
export default {
  name: "Details",
  components: {
    'singer-info': singerinfo,
    'navigation': navigation,
    'album': album,
    'MV': MV,
    'singer-details': singerDetails,
    'similarity-singer': similaritySinger
  },
  data: function data() {
    return {
      cookie: localStorage.getItem('cookie'),
      infoobj: {
        type: '歌手'
      },
      page: {
        id: '',
        type: ''
      },
      artist: {},
      //歌手信息 
      hotAlbum: [],
      //热门专辑
      MV: [],
      //MV
      navlist: ['专辑', 'MV', '歌手详情', '相似歌手'],
      navtype: 'details',
      cut: 0,
      xqparams: {
        name: '',
        briefDesc: '',
        introduction: '',
        txt: []
      },
      xsartists: [] //相似歌手	

    };
  },
  methods: {
    onclick: function onclick(value) {
      this.cut = value;
    },
    onload: function onload(value) {
      console.log('记得完善');
    }
  },
  computed: {
    ispage: function ispage() {
      return this.$store.state.page;
    }
  },
  watch: {
    ispage: {
      deep: true,
      handler: function handler(value) {
        var _this = this;

        this.page.id = value.id, this.page.type = value.type;
        getArtistAlbum({
          id: value.id
        }).then(function (res) {
          _this.hotAlbum = res.data.hotAlbums;
          _this.artist = res.data.artist;
          _this.xqparams.name = res.data.artist.name;
        });
        getArtistMv({
          id: value.id,
          limit: 30
        }).then(function (res) {
          _this.MV = res.data.mvs;
        });
        getArtistDesc({
          id: value.id
        }).then(function (res) {
          _this.xqparams.briefDesc = res.data.briefDesc;
          _this.xqparams.introduction = res.data.introduction;
          res.data.introduction.forEach(function (a) {
            _this.xqparams.txt.push(getbr(a.txt.replace(/\n/g, '<br>')));
          });
        });
        getSimiArtist({
          id: value.id,
          cookie: this.cookie
        }).then(function (res) {
          _this.xsartists = res.data.artists;
        });
      }
    }
  }
};
