import { getPlaylistDetail, getPlaylistDetailDynamic, getPlaylistTrackAll, getPlaylistSubscribers } from "../../../plugins/particulars.js";
import { gettime, getwang, getaddbr, getmiao, gettime1 } from "../../../general/regular.js";
import { getCommentNew } from "../../../plugins/comment.js";
import info from "../../../components/songlist/info.vue";
import navigation from "../../../components/basics/navigation.vue";
import songlist from "./songlist.vue";
import comment from "./comment.vue";
import collector from "./collector.vue";
export default {
  name: 'page',
  components: {
    'info': info,
    'navigation': navigation,
    'page-song-list': songlist,
    'comment': comment,
    'collector': collector
  },
  data: function data() {
    return {
      navlist: [],
      navtype: 'details',
      pageobj: {
        type: ''
      },
      page: {
        img: '',
        //歌单背景图
        name: '',
        //歌单名
        nickimg: '',
        //歌单创建者头像
        nickname: '',
        //歌单创建者
        cjtime: '',
        //创建时间
        bookedCount: '',
        //收藏人数
        playCount: '',
        //播放人数
        shareCount: '',
        //分享人数
        tags: [],
        //歌单标签
        songlength: '',
        //歌曲数
        front: '',
        //开头内容
        followup: '',
        //后续内容
        iszhansi: '' //是否展示后续

      },
      gdparticulars: '',
      //歌单详情
      gdstate: {
        commentCount: '' //评论数									

      },
      cut: 0,
      //切换
      songlist: {
        //全部歌曲
        privileges: [],
        songs: []
      },
      isc: '',
      //li点击之后添加样式
      songtime: [],
      //歌曲时长
      hotcomment: '',
      //热门评论
      newcomment: '',
      //最新评论
      collectors: [] //歌单收藏者

    };
  },
  computed: {
    ispage: function ispage() {
      return JSON.parse(JSON.stringify(this.$store.state.page));
    }
  },
  methods: {
    onclick: function onclick(value) {
      this.cut = value;
    }
  },
  watch: {
    ispage: {
      immediate: true,
      deep: true,
      handler: function handler(value) {
        var _this = this;

        this.pageobj.type = value.type;
        getPlaylistDetail({
          id: value.id
        }).then(function (res) {
          _this.page.img = res.data.playlist.coverImgUrl;
          _this.page.name = res.data.playlist.name;
          _this.page.nickimg = res.data.playlist.creator.avatarUrl;
          _this.page.nickname = res.data.playlist.creator.nickname;
          _this.page.followup = getaddbr(res.data.playlist.description).t;
          _this.page.front = getaddbr(res.data.playlist.description).d;
          _this.page.iszhansi = getaddbr(res.data.playlist.description).zhansi;
          _this.page.cjtime = gettime(res.data.playlist.createTime);
        });
        getPlaylistDetailDynamic({
          id: value.id
        }).then(function (res) {
          _this.page.bookedCount = getwang(res.data.bookedCount);
          _this.navlist = ['歌曲列表', '评论(' + res.data.commentCount + ')', '收藏者'];
          _this.page.playCount = getwang(res.data.playCount);
          _this.page.shareCount = getwang(res.data.shareCount);
        });
        getPlaylistTrackAll({
          id: value.id
        }).then(function (res) {
          _this.songlist.privileges = res.data.privileges;
          _this.songlist.songs = res.data.songs;
          _this.page.songlength = res.data.songs.length;
          res.data.songs.forEach(function (a) {
            _this.songtime.push(getmiao(a.dt));
          });
        });
        getCommentNew({
          id: value.id,
          type: 2,
          pageSize: 10,
          sortType: 2
        }).then(function (res) {
          _this.hotcomment = res.data.data;
        });
        getCommentNew({
          id: value.id,
          type: 2,
          pageSize: 60,
          sortType: 3
        }).then(function (res) {
          _this.newcomment = res.data.data;
        });
        getPlaylistSubscribers({
          id: value.id
        }).then(function (res) {
          _this.collectors = res.data.subscribers;
        });
      }
    }
  },
  mounted: function mounted() {}
};
