.input {
  margin: 10px 0 10px 0;
  background: none;
  border: none;
  outline: none;
  max-width: 190px;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 9999px;
  box-shadow: inset 2px 2px 10px rgb(255, 255, 255);
  color: #fff;
}

::-ms-input-placeholder{
	color: #e5e5e5;
}

::-webkit-input-placeholder{
	color: #e5e5e5;
}

input::placeholder{
	color: #e5e5e5;
}
